import React, { useState, useEffect, } from 'react';
import axios from 'axios'
import Slider from '@material-ui/core/Slider';
import styles from "../styles/form.module.css"
import { Sliders } from '../components/slider'

function HomePage() {
  let [testHook, setTestHook] = useState(undefined)

  //Hooks 
  let [LastMockresult, setLastMockresult] = useState(1)
  let [TargetGrade, setTargetGrade] = useState(1)
  let [PredictedGrade, setPredictedGrade] = useState(1)
  let [AttitudeToLearing, setAttitudeToLearing] = useState(1)

  //Return Hook
  let [result, setResult] = useState(undefined)

  useEffect(() => {
    (async () => {
       try{
      let data = await axios.get(window.origin + '/api/test')
      setTestHook(data.data)
    }catch(e){
      setTestHook(e)
    }
    })

  }, [])

  let sendToServer = async (e) => {
    e.preventDefault();
    let data = await axios.get(window.origin + '/api/inputTest', {LastMockresult, TargetGrade, PredictedGrade, AttitudeToLearing})
    console.log({LastMockresult, TargetGrade, PredictedGrade, AttitudeToLearing})
    setResult(Math.floor(data.data.AchievedGrade*9))
  }
  return(
    <>
      <div>Welcome to the front end!!</div>
      <h3>{ testHook ? "Server is fully loaded and functional!" : "Loading..." }</h3>
      <h1>{ result ? result : "" }</h1>
      <div className={styles.Form}>
        <Sliders max={9} title="Last Mock result" onChange={(e) => setLastMockresult(e.target.value )} />
        <Sliders max={9} title="Target Grade" onChange={(e) => setTargetGrade(e.target.value / 9)} />
        <Sliders max={9} title="Predicted Grade" onChange={(e) => setPredictedGrade(e.target.value / 9)} />
        <Sliders max={4} title="Attidue to Learning" onChange={(e) => setAttitudeToLearing(e.target.value / 9)} />
        <button onClick={sendToServer} className={styles.button}>Send to Server</button>
      </div>
    </>
  )
}

export default HomePage
